import { useState } from "react";
import axios from "axios";  // ✅ Import axios

export default function SolidityScanner() {
  const [solidityCode, setSolidityCode] = useState("");
  const [file, setFile] = useState(null);
  const [analysisResult, setAnalysisResult] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  // Handle file selection
  const handleFileChange = (event) => {
    const uploadedFile = event.target.files[0];
    setFile(uploadedFile);
    setError("");  // Clear errors
  };

  // Handle text input change
  const handleCodeChange = (event) => {
    setSolidityCode(event.target.value);
    setError("");  // Clear errors
  };

  // Submit Solidity code or file to backend using axios
  const handleSubmit = async () => {
    setLoading(true);
    setAnalysisResult("");
    setError("");

    const formData = new FormData();

    if (file) {
      console.log("📂 File selected:", file);
      formData.append("file", file, file.name);
    } else if (solidityCode.trim()) {
      console.log("📝 Solidity code submitted.");
      formData.append("code", solidityCode);
    } else {
      setError("❌ Please upload a file or enter Solidity code.");
      setLoading(false);
      return;
    }

    // ✅ Log FormData before sending
    for (const pair of formData.entries()) {
      console.log("📩 FormData:", pair[0], pair[1]);
    }

    try {
      const response = await axios.post("http://127.0.0.1:5001/analyze", formData, {
        headers: {
          "Content-Type": "multipart/form-data",  // ✅ Required for file uploads
          "Accept": "application/json",
        },
      });

      console.log("✅ Server Response:", response.data);
      setAnalysisResult(response.data.analysis);
    } catch (err) {
      console.error("❌ Axios Error:", err);
      setError(err.response?.data?.error || "Something went wrong.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-800 flex items-center justify-center p-4">
      <div className="w-full max-w-3xl bg-gray-900 text-white rounded-lg shadow-xl p-8 overflow-auto">
        <h2 className="text-2xl font-bold mb-6 text-center">
          🔍 Solidity Security Scanner
        </h2>

        {/* File Upload Input */}
        <div className="mb-6">
          <label className="block text-sm font-medium mb-1" htmlFor="fileUpload">
            Upload Solidity File
          </label>
          <input
            id="fileUpload"
            type="file"
            accept=".sol"
            className="w-full p-2 border rounded bg-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-400"
            onChange={handleFileChange}
          />
        </div>

        {/* OR Paste Solidity Code */}
        <div className="mb-6">
          <label className="block text-sm font-medium mb-1" htmlFor="solidityCode">
            Or Paste Solidity Code
          </label>
          <textarea
            id="solidityCode"
            className="w-full p-3 bg-gray-700 rounded border focus:outline-none focus:ring-2 focus:ring-blue-400 resize-none"
            rows="8"
            placeholder="Paste your Solidity code here..."
            value={solidityCode}
            onChange={handleCodeChange}
          ></textarea>
        </div>

        {/* Submit Button */}
        <div className="mb-6 text-center">
          <button
            onClick={handleSubmit}
            className="w-full sm:w-auto bg-blue-500 hover:bg-blue-600 text-white font-bold py-3 px-6 rounded focus:outline-none focus:ring-2 focus:ring-blue-400"
            disabled={loading}
          >
            {loading ? "Analyzing..." : "Analyze Contract"}
          </button>
        </div>

        {/* Error Message */}
        {error && (
          <div className="mb-6 p-4 bg-red-600 text-white rounded">
            <strong>⚠️ Error:</strong> {error}
          </div>
        )}

        {/* Display Analysis Result */}
        {analysisResult && (
          <div className="p-6 bg-gray-800 rounded overflow-auto">
            <h3 className="text-xl font-bold mb-4">🧠 AI Analysis:</h3>
            <pre className="whitespace-pre-wrap text-sm">
              {analysisResult}
            </pre>
          </div>
        )}
      </div>
    </div>
  );

}
