import { useState } from "react";
import axios from "axios";  // ‚úÖ Import axios

export default function SolidityScanner() {
  const [solidityCode, setSolidityCode] = useState("");
  const [file, setFile] = useState(null);
  const [analysisResult, setAnalysisResult] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  // Handle file selection
  const handleFileChange = (event) => {
    const uploadedFile = event.target.files[0];
    setFile(uploadedFile);
    setError("");  // Clear errors
  };

  // Handle text input change
  const handleCodeChange = (event) => {
    setSolidityCode(event.target.value);
    setError("");  // Clear errors
  };

  // Submit Solidity code or file to backend using axios
  const handleSubmit = async () => {
    setLoading(true);
    setAnalysisResult("");
    setError("");

    const formData = new FormData();

    if (file) {
      console.log("üìÇ File selected:", file);
      formData.append("file", file, file.name);
    } else if (solidityCode.trim()) {
      console.log("üìù Solidity code submitted.");
      formData.append("code", solidityCode);
    } else {
      setError("‚ùå Please upload a file or enter Solidity code.");
      setLoading(false);
      return;
    }

    // ‚úÖ Log FormData before sending
    for (const pair of formData.entries()) {
      console.log("üì© FormData:", pair[0], pair[1]);
    }

    try {
      const response = await axios.post("http://127.0.0.1:5001/analyze", formData, {
        headers: {
          "Content-Type": "multipart/form-data",  // ‚úÖ Required for file uploads
          "Accept": "application/json",
        },
      });

      console.log("‚úÖ Server Response:", response.data);
      setAnalysisResult(response.data.analysis);
    } catch (err) {
      console.error("‚ùå Axios Error:", err);
      setError(err.response?.data?.error || "Something went wrong.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto mt-10 p-6 bg-gray-900 text-white rounded-lg shadow-lg">
      <h2 className="text-xl font-semibold mb-4">üîç Solidity Security Scanner</h2>

      {/* File Upload Input */}
      <input
        type="file"
        accept=".sol"
        className="mb-4 p-2 border rounded w-full bg-gray-700"
        onChange={handleFileChange}
      />

      {/* OR Paste Solidity Code */}
      <textarea
        className="w-full p-2 bg-gray-700 rounded border mb-4"
        rows="6"
        placeholder="Paste your Solidity code here..."
        value={solidityCode}
        onChange={handleCodeChange}
      ></textarea>

      {/* Submit Button */}
      <button
        onClick={handleSubmit}
        className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded"
        disabled={loading}
      >
        {loading ? "Analyzing..." : "Analyze Contract"}
      </button>

      {/* Error Message */}
      {error && (
        <div className="mt-4 p-3 bg-red-600 text-white rounded">
          <strong>‚ö†Ô∏è Error:</strong> {error}
        </div>
      )}

      {/* Display Analysis Result */}
      {analysisResult && (
        <div className="mt-6 p-4 bg-gray-800 rounded">
          <h3 className="text-lg font-bold">üß† AI Analysis:</h3>
          <pre className="whitespace-pre-wrap">{analysisResult}</pre>
        </div>
      )}
    </div>
  );
}
